package com.suresh.basics.string.encoding;

import java.util.ArrayList;
import java.util.List;

public class StringToHex {
	
	public String fromStringtoHex(String s) {
		StringBuilder sb = new StringBuilder();
		for(int c : s.toCharArray()) {
			sb.append(Integer.toHexString(c));
		}
		return sb.toString();
	}
	
	public String fromHexToString(String s) {
		int len = s.length();
		StringBuilder sb = new StringBuilder();
		for(int i=0; i < len-1; i += 2) {
			String val = s.substring(i, i+2);
			char c = (char) Integer.valueOf(val, 16).intValue();
			sb.append(""+c);
		}
		return sb.toString();
	}
	
	public static void main(String args[]) {
		List<String> sList = new ArrayList<String>();
		sList.add("");
		
		StringToHex sth = new StringToHex();
//		String temp = sth.fromStringtoHex(s);
//		System.out.println(temp);
		for(String s : sList) {
			System.out.println(sth.fromHexToString(s));
		}
	}

}
